import React, { Component } from 'react';
import PropTypes from 'prop-types';

export class AddCard extends Component {
  state = {
    name: '',
    description: '',
    timeDuration: '',
    parentCard: '',
    arrayOfAttributes: ''
  }

  onSubmit = (e) => {
    e.preventDefault();
    this.props.addCard(this.state.name,this.state.description,this.state.timeDuration,this.state.parentCard,this.state.arrayOfAttributes);
    this.setState({ name: '',
      description: '',
      timeDuration: '',
      parentCard: '',
      arrayOfAttributes: ''});
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  render() {
    return (
      <form onSubmit={this.onSubmit} style={{ display: 'flex' }}>
        <input 
          type="text" 
          name="title" 
          style={{ flex: '10', padding: '5px' }}
          placeholder="name"
          value={this.state.name}
          onChange={this.onChange}
        />
          <input
              type="text"
              name="description"
              style={{ flex: '10', padding: '5px' }}
              placeholder="description"
              value={this.state.description}
              onChange={this.onChange}
          />
        <input 
          type="submit" 
          value="Submit" 
          className="btn"
          style={{flex: '1'}}
        />
      </form>
    )
  }
}

// PropTypes
AddCard.propTypes = {
  addCard: PropTypes.func.isRequired
}

export default AddCard
